        o.setDataretirada(txtDataretirada.getText());
        o.setDataentrega(txtDataentrega.getText());
        o.setValordiaria(txtValordiaria.getText());
        o.setValortotal(txtValortotal.getText());
        
        OrcamentoController oc = OrcamentoController();  
        
